DELIMITER $$
CREATE PROCEDURE `IGC_GetLoadEventInventory`(
	p_AccountID	nvarchar(10)
,	p_Name		nvarchar(10)
)
BEGIN  
	DECLARE v_Inventory VARBINARY(1024);    
	DECLARE v_OldAccountID nvarchar(10);
	
    SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
    
	SELECT AccountID INTO v_OldAccountID FROM T_Event_Inventory WHERE Name = p_Name;
	IF v_OldAccountID <> p_AccountID 
	THEN
		DELETE FROM T_Event_Inventory WHERE AccountID = v_OldAccountID and Name = p_Name;
	END IF;
	
	
	SELECT Inventory INTO v_Inventory FROM T_Event_Inventory WHERE AccountID = p_AccountID AND Name = p_Name;
	IF FOUND_ROWS() = 0
	THEN
		SET v_Inventory = ;
		
		INSERT INTO T_Event_Inventory ( AccountID, Name, Inventory)
		VALUES (p_AccountID,p_Name,v_Inventory);
	END IF;

	SELECT v_Inventory;
END$$
DELIMITER ;
